import React, {Component} from 'react';
import {BrowserRouter as Router} from 'react-router-dom';
import {Switch, Route} from 'react-router-dom';
import Home from './components/Home';
import UserProfile from './components/UserProfile'
import LogIn from './components/Login';

class App extends Component {
  constructor(props) {
    super();

    this.state = {
      accountBalance: 0,
      currentUser: {
        userName: 'joe_shmo',
        memberSince: '07/23/96',
      },
      debits: [],
      credits: [],

    };
  }

  componentDidMount() {
    fetch('https://moj-api.herokuapp.com/debits')
      .then(response => response.json())
      .then(data => this.setState({debits: data}))

    fetch('https://moj-api.herokuapp.com/credits')
      .then(response=> response.json())
      .then(data => this.setState({credits: data}))
  }

  componentDidUpdate() {
    let total = 0

    this.state.credits.map(credit => total += +credit.amount)

    this.state.debits.map(debit => total -= +debit.amount)

    this.setState({accountBalance: total})

  }

  mockLogIn = (logInInfo) => {
    const newUser = { ...this.state.currentUser };
    newUser.userName = logInInfo.userName;
    this.setState({ currentUser: newUser });
  };

  addDebit = () => {}
  addCredit = () => {}

  render() {
    return (
      <Router>
        <Switch>
          <Route exact path='/' render={() => <Home accountBalance={this.state.accountBalance} />} />
          <Route 
          exact 
          path='/login' render={() => <LogIn 
            user={this.state.currentUser} 
            mockLogIn={this.mockLogIn} />} 
          />
          <Route
            exact
            path='/userProfile'
            render={() => <UserProfile 
                userName={this.state.currentUser.userName} 
                memberSince={this.state.currentUser.memberSince}  />}
          />
          <Route
            exact
            path='/accountbalance'
            render={() => <h1> Account Balance Route </h1>}
          />
        </Switch>
      </Router>
    );
  }
}

export default App;
