import React, { Component } from 'react'

class Debits extends Component {
  constructor(props) {
    super(props);

    this.state = {
      date: '',
      id: '',
      description: '',
      amount: 0,
    };
  }

  render() {
    const { debits } = this.props;

    const drawTable = () =>
      debits.map((debit) => {
        return (
          <tr key={debit.id}>
            <td>{debit.date}</td>
            <td style={{ padding: '0 3rem' }}>{debit.description}</td>
            <td>{debit.amount}</td>
          </tr>
        );
      });

    return (
      <section>
        <h1>Debits</h1>

        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Description</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>{drawTable()}</tbody>
        </table>

        <form>
          <label>Description:</label>
          <input type='text'></input>
          <label>Amount:</label>
          <input
            type='text'
            value={this.state.amount}
            onChange={(val) => setState({ amount: val })}
          />
        </form>
      </section>
    );
  }
}

export default Debits
