import React, { Component } from 'react'
import { uuid } from 'uuidv4'

class Debits extends Component {
  constructor(props) {
    super(props);

    this.state = {
      date: '',
      id: '',
      description: '',
      amount: '',
    };
  }

  addDebit = (e) => {
    e.preventDefault()
    console.log(this.state.amount)


    this.props.addDebit({
      id: uuid(),
      date: new Date().toUTCString(),
      description: this.state.description,
      amount: +this.state.amount
    });
  }

  render() {
    const { debits } = this.props;

    const drawTable = () =>
      debits.map((debit) => {
        return (
          <tr key={debit.id}>
            <td>{debit.date}</td>
            <td style={{ padding: '0 3rem' }}>{debit.description}</td>
            <td>{debit.amount}</td>
          </tr>
        );
      });

    return (
      <section>
        <h1>Debits</h1>

        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Description</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>{drawTable()}</tbody>
        </table>

        <form onSubmit={this.addDebit}>
          <label>Description:</label>
          <input 
            type='text' 
            value={this.state.description} 
            onChange={(val) => this.setState({description: val.targetValue})} />
          <label>Amount:</label>
          <input
            type='text'
            value={this.state.amount}
            onChange={(val) => this.setState({ amount: val.targetValue })}
          />
          <button>Add Debit</button>
        </form>
      </section>
    );
  }
}

export default Debits
